import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

export default function ViewProducto() {
  const [producto, setProducto] = useState(null); // Cambiar a null para manejar el estado de carga
  const { id } = useParams();

  useEffect(() => {
    loadProducto();
  }, []);

  const loadProducto = async () => {
    try {
      const result = await axios.get(`http://localhost:8080/producto/${id}`);
      setProducto(result.data);
    } catch (error) {
      console.error("Error al cargar el producto:", error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
          <h2 className="text-center m-4">Detalles del producto</h2>

          <div className="card">
            <div className="card-header">
              {producto ? (
                <div>
                  <h5 className="card-title">Detalles del producto ID: {producto.id}</h5>
                  <ul className="list-group list-group-flush">
                    <li className="list-group-item">
                      <b>Título:</b> {producto.titulo}
                    </li>
                    <li className="list-group-item">
                      <b>Descripción:</b> {producto.descripcion}
                    </li>
                    <li className="list-group-item">
                      <b>Estado:</b> {producto.estado}
                    </li>
                    <li className="list-group-item">
                      <b>Cantidad:</b> {producto.cantidad}
                    </li>
                    <li className="list-group-item">
                      <b>Categoría:</b> {producto.categoria_id}
                    </li>
                    <li className="list-group-item">
                      <b>Imagen:</b>
                      <img
                        src={`http://localhost:8080${producto.imagen}`}
                        alt={producto.titulo}
                        className="card-img-top mt-3"
                        style={{
                          height: '200px',
                          objectFit: 'contain'
                        }}
                      />
                    </li>
                  </ul>
                </div>
              ) : (
                <Skeleton height={200} style={{ borderRadius: '10px' }} />
              )}
            </div>
          </div>
          <Link className="btn btn-primary my-2" to={"/"}>
            Volver al inicio
          </Link>
        </div>
      </div>
    </div>
  );
}
