import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';

export default function MisProductos() {
    const [productos, setProducto] = useState([]);

    useEffect(() => {
        loadProductos();
    }, []);

    const loadProductos = async () => {
        try {
            const response = await axios.get("http://localhost:8080/productos"); // Endpoint para obtener los productos del usuario actual
            setProducto(response.data);
        } catch (error) {
            console.error('Error al cargar los productos:', error);
        }
    };

    const deleteProducto = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/producto/${id}`);
            loadProductos();
        } catch (error) {
            console.error('Error al eliminar el producto:', error);
        }
    }

    return (
        <div className="container my-5">
            <div className="py-4">
                <h2 className="mb-4">Mis Productos</h2>
                <div className="row">
                    {productos.map((producto, index) => (
                        <div className="col-md-4 mb-4" key={index}>
                            <div className="card shadow-sm h-100">
                                <img 
                                    src={`http://localhost:8080${producto.imagen}`} 
                                    alt={producto.titulo} 
                                    className="card-img-top" 
                                    style={{ height: '200px', objectFit: 'cover' }} 
                                />
                                <div className="card-body d-flex flex-column">
                                    <h5 className="card-title">{producto.titulo}</h5>
                                    <p className="card-text">{producto.descripcion}</p>
                                    <div className="mt-auto">
                                        <div className="d-flex justify-content-between align-items-center">
                                            <div className="btn-group">
                                                <Link className="btn btn-sm btn-primary" to={`/viewproducto/${producto.id}`}>Ver</Link>
                                                <Link className="btn btn-sm btn-outline-primary mx-1" to={`/editproducto/${producto.id}`}>Editar</Link>
                                                <button className="btn btn-sm btn-danger" onClick={() => deleteProducto(producto.id)}>Eliminar</button>
                                            </div>
                                        </div>
                                        <small className="text-muted">Estado: {producto.estado}</small><br />
                                        <small className="text-muted">Cantidad: {producto.cantidad}</small><br />
                                        <small className="text-muted">Categoría: {producto.categoria}</small>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
